shell.enable:
if [ /sbin/hwclock --hctosys ${configuration_program_options_hwclock} ] ; then
                echo -n 'feedback|notice|time read from hardware clock' ;
          else 
                echo -n 'feedback|warning|failed to read time from hardware clock' ;
          fi

shell.disable:
if [ /sbin/hwclock --systohc ${configuration_program_options_hwclock} ] ; then
                echo -n 'feedback|notice|time saved to hardware clock' ;
          else 
                echo -n 'feedback|warning|failed to save time to hardware clock' ;
          fi
