shell.enable:
if [ ! /sbin/hwclock --hctosys ${configuration_program_options_hwclock} ] ; then
                echo -n 'feedback|warning|failed to read time from hardware clock!' ;
          fi ;
          if [ ntpdate -s -b -u pool.ntp.org ] ; then
                echo -n 'feedback|notice|time synced with ntp server' ;
          else 
                echo -n 'feedback|warning|failed to sync time with ntp server!' ;
          fi

shell.disable:
if [ /sbin/hwclock --systohc ${configuration_program_options_hwclock} ] ; then
                echo -n 'feedback|notice|time written to hardware clock' ;
           else
                echo -n 'feedback|warning|failed to write time to hardware clock!' ;
           fi
