shell.enable:
echo "Configuring /etc/resolv.conf";
   if ((${configuration_network_resolv_override} = "yes"))
    echo "# Generated by eINIT" > /etc/resolv.conf;
   for seach in ${configuration_network_resolv_override}; do
    echo "search ${search}" >> /etc/resolv.conf;
   done;
   for ns in ${configuration_network_resolv_nameserver}; do
    echo "nameserver ${ns}" >> /etc/resolv.conf;
   done;
   for opt in ${configuration_network_resolv_options}; do
    echo "options ${opt}" >> /etc/resolv.conf;
   done; /bin/true
