shell.prepare:
if test "${configuration_feedback_visual_fbsplash_daemon_mount_dir_b}" = "yes"; then
       umount splashcache;
       mount splashcache -t tmpfs "${configuration_feedback_visual_fbsplash_daemon_mount_dir_path}" -o size=${configuration_feedback_visual_fbsplash_daemon_mount_dir_size};
      fi;
      if test ! -e /dev/fb0; then
       mknod /dev/fb0 c 29 0;
       chown root:video /dev/fb0;
       chmod 0660 /dev/fb0;
      fi;
      /bin/true

shell.enable:
pexec-options dont-close-stdin;
      for TTY in ${configuration_feedback_visual_fbsplash_ttys} ; do
       /sbin/splash_util.static --tty=${TTY} -m v -t ${configuration_feedback_visual_fbsplash_theme} -c setcfg ;
       /sbin/splash_util.static --tty=${TTY} -m v -t ${configuration_feedback_visual_fbsplash_theme} -c setpic 2>/dev/null ;
       /sbin/splash_util.static --tty=${TTY} -c on ;
      done;

      if /sbin/splash_util -d -t ${configuration_feedback_visual_fbsplash_theme}; then exit 0; fi;
      /sbin/splash_util.static -d -t ${configuration_feedback_visual_fbsplash_theme};
