shell.enable:
echo -n "feedback|notice|Initializing random number generator...";
          if [ -f ${configuration_system_urandom_random_seed} ]; then
           cat ${configuration_system_urandom_random_seed} >/dev/urandom;
          else
           touch ${configuration_system_urandom_random_seed};
          fi;
          chmod 600 ${configuration_system_urandom_random_seed};
          dd if=/dev/urandom of=${configuration_system_urandom_random_seed} count=1 bs=512 &>/dev/null

shell.disable:
echo -n "feedback|notice|Saving random seed...";
            touch ${configuration_system_urandom_random_seed};
            chmod 600 ${configuration_system_urandom_random_seed};
            dd if=/dev/urandom of=${configuration_system_urandom_random_seed} count=1 bs=512 &>/dev/null
