scriptlets.init:
source ${splash-functions-file};
RUNLEVEL='S';
SOFTLEVEL='${new-mode}';

splash rc_init sysinit;
splash rc_init boot;

scriptlets.action:
source ${splash-functions-file};
RUNLEVEL='3';
SOFTLEVEL='${new-mode}';

splash_svclist_get() {
 if [[ '$1' == 'start' ]]; then
  echo ${all-services};
 fi
};

splash_svclist_update() {
 echo ${active-services};
};

splash_setup;
${action};
