shell.enable:
echo 'feedback|notice|Setting up Intel resolution...';
          915resolution ${configuration_services_video_intel_resolution_mode} ${configuration_services_video_intel_resolution_x} ${configuration_services_video_intel_resolution_y} ${configuration_services_video_intel_resolution_bpp} >> /dev/null;
          if [[ $? == '0' ]]; then
          	echo -n ' OK';
          else
          	echo 'feedback|error|Intel resolution failed!';
          fi
