daemon.prepare:
if [ ! -e /proc/acpi ]; then
                echo 'feedback|error|ACPI support has not been compiled into the kernel!';
           fi

daemon.command:
pexec-options dont-close-stdin;if /usr/sbin/acpid ${configuration_services_acpid_options}; then
                 pidof acpid > /var/run/acpid.pid;
           fi
