execute.code:
local clamd_socket=$(awk '$1 == "LocalSocket" { print $2 }' /etc/clamd.conf);
           logfile=$(awk '$1 == "LogFile" { print $2 }' /etc/clamd.conf);
           clamav_user=$(awk '$1 == "User" { print $2 }' /etc/clamd.conf);
           if [[ -n "${logfile}" && -n "${clamav_user}" ]]; then 
           	if [ ! -f "${logfile}" ]; then
           		touch ${logfile};
           	fi;
           	chown ${clamav_user} ${logfile};
           fi;
           if [ -S "${clamd_socket:-/tmp/clamd}" ]; then
           	rm -f ${clamd_socket:-/tmp/clamd};
           fi;
