execute.code:
logfile=$(awk '$1 == "UpdateLogFile" { print $2 }' /etc/freshclam.conf);
           if [[ -n "${logfile}" && -n "${clamav_user}" ]]; then
           	if [ ! -f "${logfile}" ]; then
           		touch ${logfile};
           	fi;
           	chown ${clamav_user} ${logfile};
           fi;
