shell.enable:
/sbin/hwclock --hctosys ${configuration_services_hwclock_options};
          if [[ $? == '0' ]]; then
          	echo 'feedback|notice|time read from hardware clock';
          else
         	echo 'feedback|warning|failed to read time from hardware clock';
          fi

shell.disable:
/sbin/hwclock --systohc ${configuration_services_hwclock_options}
           if [[ $? == '0' ]]; then
           	echo 'feedback|notice|time written to hardware clock';
           else
           	echo 'feedback|warning|failed to write time to hardware clock';
           fi
