execute.code:
echo "feedback|notice|Initializing random number generator...";
          if [ -f ${configuration_services_urandom_seed} ]; then
           cat ${configuration_services_urandom_seed} >/dev/urandom;
          else
           touch ${configuration_services_urandom_seed};
          fi;
          chmod 600 ${configuration_services_urandom_seed};
          dd if=/dev/urandom of=${configuration_services_urandom_seed} count=1 bs=512 &>/dev/null

echo "feedback|notice|Saving random seed...";
            touch ${configuration_services_urandom_seed};
            chmod 600 ${configuration_services_urandom_seed};
            dd if=/dev/urandom of=${configuration_services_urandom_seed} count=1 bs=512 &>/dev/null
